import React, { useState } from 'react';
import ClaimForm from './components/ClaimForm';
import ClaimStatus from './components/ClaimStatus';
import AdminPanel from './components/AdminPanel';
import TransactionHistory from './components/TransactionHistory';

const App = () => {
  const [claimId] = useState('example-claim-id'); // Replace with actual claim ID
  const [claimStatus, setClaimStatus] = useState('Pending');
  const [transactions, setTransactions] = useState([]);

  const handleClaimSubmission = (formData) => {
    console.log('Submitting claim...', formData);
    // Here you can simulate adding a transaction
    setTransactions([...transactions, `Claim submitted by ${formData.patientName}`]);
  };

  const handleApprove = (id) => {
    console.log(`Claim ${id} approved`);
    setClaimStatus('Approved');
  };

  const handleReject = (id) => {
    console.log(`Claim ${id} rejected`);
    setClaimStatus('Rejected');
  };

  return (
    <div>
      <h1>Blockchain-Based Medical Insurance Claim DApp</h1>
      <ClaimForm onSubmitClaim={handleClaimSubmission} />
      <ClaimStatus claimId={claimId} status={claimStatus} />
      <AdminPanel claimId={claimId} onApprove={handleApprove} onReject={handleReject} />
      <TransactionHistory transactions={transactions} />
    </div>
  );
};

export default App;
