import React, { useEffect, useState } from 'react';
import { getClaimStatus } from '../cosmwasm/blockchainConnection';

const ClaimStatus = ({ claimId }) => {
  const [status, setStatus] = useState('');

  useEffect(() => {
    const fetchClaimStatus = async () => {
      const claimStatus = await getClaimStatus(claimId);
      setStatus(claimStatus);
    };

    fetchClaimStatus();
  }, [claimId]);

  return (
    <div>
      <h2>Claim Status</h2>
      <p>Status for Claim ID {claimId}: {status}</p>
    </div>
  );
};

export default ClaimStatus;
